const UserContext = createContext();

export default function Context() {

        const [user, setUser] = useState("Jesse Hall");

        return (
          <UserContext.Provider value={user}>
            <h1>{`Hello ${user}!`}</h1>
            <Component2 />
          </UserContext.Provider>
        );


      function Component2() {
        return (
          <>
            <h1>Component 2</h1>
            <Component3 />
          </>
        );
      }

      function Component3() {
        return (
          <>
            <h1>Component 3</h1>
            <Component4 />
          </>
        );
      }

