            <h1>Component 4</h1>
            <Component5 />
          </>
        );
      }

      function Component5() {
        const user = useContext(UserContext);

        return (
          <>
            <h1>Component 5</h1>
            <h2>{`Hello ${user} again!`}</h2>
          </>
        );
      }
}
