import React, { useState, useEffect } from 'react';
import axios from "axios";
import { useLocation } from 'react-router-dom';
import { Form } from 'react-router-dom';
export default function AddEdit() {
  const location = useLocation();
  const id = location.state;
  const [employee, setEmployee] = useState({});
  const authAxios = axios.create({ baseURL: 'https://departments-a9275-default-rtdb.firebaseio.com/' })
  useEffect(() => {

    fetchData(id);

  }, [id])

  function fetchData(id) {
    id = id - 1;
    authAxios.get(`/${id}.json`).then((response) => {
      setEmployee(response.data);
    })
  }
  return (
    <form>
      <div class="mb-3">
        <label for="exampleInputEmail1" class="form-label">ID</label>
        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" disabled/>
      </div>
      <div class="mb-3">
        <label for="exampleInputPassword1" class="form-label">Name</label>
        <input type="password" class="form-control" vLU id="exampleInputPassword1"/>
      </div>
      <div class="mb-3">
        <label for="exampleInputPassword1" class="form-label">Age</label>
        <input type="password" class="form-control" id="exampleInputPassword1"/>
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
    </form>
  )
}
