import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, query, orderByChild, equalTo, remove, onValue } from 'firebase/database';
import Table from 'react-bootstrap/Table';

// Firebase configuration (same as above
const firebaseConfig = {
  apiKey: "AIzaSyCHGpnCBUeVfVRljnY33oCQCxfrpBM8liA",
    authDomain: "departments-a9275.firebaseapp.com",
    projectId: "departments-a9275",
    storageBucket: "departments-a9275.appspot.com",
    messagingSenderId: "58175864054",
    appId: "1:58175864054:web:f7ed63a8484b56333540d7",
    measurementId: "G-2CF0YTGP0R",
    databaseURL: "https://departments-a9275-default-rtdb.firebaseio.com/"
};

// Initialize Firebase

const SearchDelete = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const dbRef = ref(database);
    const unsubscribe = onValue(dbRef, (snapshot) => {
      const dbData = snapshot.val();
      setData(dbData);
    });

    // Cleanup the listener on unmount
    return () => unsubscribe();
  }, []);
  const deleteDataById = (id) => {
    const dbRef = ref(database);
    // Query items with ID equal to 3
    const itemsQuery = query(dbRef, orderByChild('id'), equalTo(id));

    onValue(itemsQuery, (snapshot) => {
      snapshot.forEach((childSnapshot) => {
        // For each item, delete it
        remove(ref(database, `${childSnapshot.key}`))
          .then(() => {
            console.log("Data deleted successfully!");
          })
          .catch((error) => {
            console.error("Error deleting data: ", error);
          });
      });
    }, {
      onlyOnce: true // This ensures the listener is called only once
    });
  };


  return (
    <Table striped bordered hover>
    <thead>
      <tr>
        <th>ID</th>
        <th>NAME</th>
        <th>AGE</th>
      </tr>
    </thead>
    <tbody>
    {
      data.map((row) =>(
        <tr key={row.ID}>
        <td>{row.ID}</td>
        <td>{row.Name}</td>
        <td>{row.age}</td>
        <td><button type="button" onClick={() => deleteDataById(row.ID)} className="btn btn-danger">Delete</button>
</td>
      </tr>

    ))}
    </tbody>
  </Table>

  );
};

export default FetchDataComponent;
